import React from "react";

const Button = React.forwardRef(({
    children,
    className = "",
    variant = "primary",
    size = "md",
    disabled = false,
    onClick,
    type = "button",
    ...props
}, ref) => {
    // Base classes for all buttons
    const baseClasses = "rounded-md font-medium transition-all duration-300 flex items-center justify-center focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-opacity-50";

    // Variant classes
    const variantClasses = {
        primary: "bg-cyan-500 hover:bg-cyan-600 text-white focus:ring-cyan-500",
        secondary: "bg-gray-700 hover:bg-gray-600 text-white focus:ring-gray-500",
        success: "bg-emerald-500 hover:bg-emerald-600 text-white focus:ring-emerald-500",
        danger: "bg-red-500 hover:bg-red-600 text-white focus:ring-red-500",
        warning: "bg-amber-500 hover:bg-amber-600 text-white focus:ring-amber-500",
        buttonGray: "bg-gray-700 hover:bg-gray-600 text-white focus:ring-gray-500",
        outline: "bg-transparent border border-gray-600 text-gray-300 hover:bg-gray-700 focus:ring-gray-500",
        ghost: "bg-transparent text-gray-300 hover:bg-gray-800 focus:ring-gray-500",
        gradient: "btn-gradient text-white focus:ring-cyan-500",
        link: "bg-transparent text-cyan-400 hover:text-cyan-300 underline hover:bg-transparent p-0 focus:ring-transparent",
    };

    // Size classes
    const sizeClasses = {
        sm: "text-xs px-3 h-8",
        md: "text-sm px-4 h-10",
        lg: "text-base px-5 h-12",
        xl: "text-lg px-6 h-14",
    };

    // Disabled styles
    const disabledClasses = "opacity-50 cursor-not-allowed";

    return (
        <button
            ref={ref}
            type={type}
            className={`
        ${baseClasses}
        ${variantClasses[variant] || variantClasses.primary}
        ${sizeClasses[size] || sizeClasses.md}
        ${disabled ? disabledClasses : ""}
        ${className}
      `}
            disabled={disabled}
            onClick={disabled ? undefined : onClick}
            {...props}
        >
            {variant === 'gradient' ? <span>{children}</span> : children}
        </button>
    );
});

Button.displayName = "Button";

export { Button };
