import React from 'react';
import Timer from './Timer';
import TaskList from './TaskList';
import { Card, CardContent, CardHeader, CardTitle } from './ui/Card';
import { Clock, CheckCircle, BarChart } from 'lucide-react';

const FocusView = ({
    tasks,
    currentTaskIndex,
    timeRemaining,
    isTimerActive,
    isBreakTime,
    onRemoveTask,
    onStartTask,
    onMasterPlayPause,
    onSkipTimer,
    onCompleteTask,
    onExtendTimer,
    activeTaskOriginalId,
    dailyStats
}) => {
    // Get the current active task if there is one
    const currentTask = currentTaskIndex >= 0 && currentTaskIndex < tasks.length
        ? tasks[currentTaskIndex]
        : null;

    return (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            <div className="lg:col-span-2">
                {/* Timer section */}
                <Timer
                    currentTask={currentTask}
                    timeRemaining={timeRemaining}
                    isTimerActive={isTimerActive}
                    isBreakTime={isBreakTime}
                    onPlayPause={onMasterPlayPause}
                    onSkip={onSkipTimer}
                    onComplete={onCompleteTask}
                    onExtend={onExtendTimer}
                />

                {/* Daily stats */}
                <Card
                    variant="subtle"
                    className="mt-6"
                >
                    <CardHeader className="pb-2">
                        <CardTitle className="flex items-center text-base">
                            <BarChart className="h-4 w-4 mr-2 text-accent-yellow" />
                            Today's Progress
                        </CardTitle>
                    </CardHeader>
                    <CardContent>
                        <div className="grid grid-cols-3 gap-4">
                            <div className="text-center">
                                <div className="text-accent-green flex justify-center mb-1">
                                    <CheckCircle className="h-5 w-5" />
                                </div>
                                <div className="text-xl font-bold text-lightText">
                                    {dailyStats.tasksCompleted}
                                </div>
                                <div className="text-xs text-subtleText">
                                    Tasks Completed
                                </div>
                            </div>

                            <div className="text-center">
                                <div className="text-cyanAccent flex justify-center mb-1">
                                    <Clock className="h-5 w-5" />
                                </div>
                                <div className="text-xl font-bold text-lightText">
                                    {Math.round(dailyStats.focusMinutes)}
                                </div>
                                <div className="text-xs text-subtleText">
                                    Minutes Focused
                                </div>
                            </div>

                            <div className="text-center">
                                <div className="text-accent-purple flex justify-center mb-1">
                                    <BarChart className="h-5 w-5" />
                                </div>
                                <div className="text-xl font-bold text-lightText">
                                    {dailyStats.score}
                                </div>
                                <div className="text-xs text-subtleText">
                                    Points Earned
                                </div>
                            </div>
                        </div>
                    </CardContent>
                </Card>
            </div>

            <div className="lg:col-span-1">
                {/* Task list */}
                <TaskList
                    tasks={tasks}
                    onRemoveTask={onRemoveTask}
                    onStartTask={onStartTask}
                    currentTaskIndex={currentTaskIndex}
                    activeTaskOriginalId={activeTaskOriginalId}
                    isTimerActive={isTimerActive}
                    isBreakTime={isBreakTime}
                    timeRemaining={timeRemaining}
                />
            </div>
        </div>
    );
};

export default FocusView;
