import * as React from "react";

import { cn } from "../../lib/utils"; // Adjusted path

const Input = React.forwardRef(({
    className,
    type,
    variant = "default",
    size = "default",
    leftIcon,
    rightIcon,
    error,
    ...props
}, ref) => {
    // Base classes all inputs share
    const baseClasses = "flex w-full rounded-md border text-sm transition-all duration-200 focus-visible:outline-none";

    // Size variations
    let sizeClasses = "";
    if (size === "sm") {
        sizeClasses = "h-8 px-3 py-1 text-xs";
    } else if (size === "default") {
        sizeClasses = "h-10 px-4 py-2";
    } else if (size === "lg") {
        sizeClasses = "h-12 px-4 py-3 text-base";
    }

    // Variant styles
    let variantClasses = "";
    if (variant === "default") {
        variantClasses = "bg-dark-200/80 border-dark-300/40 text-lightText placeholder:text-gray-500 focus:border-cyanAccent focus:ring-1 focus:ring-cyanAccent shadow-sm hover:bg-dark-200/90";
    } else if (variant === "filled") {
        variantClasses = "bg-dark-100 border-dark-300/40 text-lightText placeholder:text-gray-500 focus:border-cyanAccent focus:ring-1 focus:ring-cyanAccent shadow-inner hover:bg-dark-100/90";
    } else if (variant === "outline") {
        variantClasses = "bg-transparent border-dark-300/80 text-lightText placeholder:text-gray-500 focus:border-cyanAccent focus:ring-1 focus:ring-cyanAccent";
    } else if (variant === "ghost") {
        variantClasses = "bg-transparent border-transparent text-lightText placeholder:text-gray-500 focus:bg-dark-200/30 focus:border-cyanAccent/40";
    }

    // Error state overrides border color
    if (error) {
        variantClasses = variantClasses.replace("border-dark-300", "border-buttonRed").replace("focus:border-cyanAccent", "focus:border-buttonRed").replace("focus:ring-cyanAccent", "focus:ring-buttonRed");
    }

    // If we have icons, add padding
    const leftIconClasses = leftIcon ? "pl-10" : "";
    const rightIconClasses = rightIcon ? "pr-10" : "";

    return (
        <div className="relative w-full">
            {leftIcon && (
                <div className="absolute left-3 top-1/2 -translate-y-1/2 text-subtleText">
                    {leftIcon}
                </div>
            )}
            <input
                type={type}
                className={cn(baseClasses, sizeClasses, variantClasses, leftIconClasses, rightIconClasses, className)}
                ref={ref}
                {...props}
            />
            {rightIcon && (
                <div className="absolute right-3 top-1/2 -translate-y-1/2 text-subtleText">
                    {rightIcon}
                </div>
            )}
            {error && (
                <p className="mt-1 text-xs font-medium text-buttonRed">{error}</p>
            )}
        </div>
    );
});

Input.displayName = "Input";

export { Input };
