import React from 'react';
import { Button } from './ui/Button';
import { ArrowRightCircle, Trash2 } from 'lucide-react';
import { cn } from '../lib/utils';

const SpiralItem = ({
    spiral,
    onRemoveSpiral,
    onMoveSpiralToTasks,
    theme
}) => {

    const itemNameColor = theme === 'dark' ? 'text-lightText' : 'text-darkText';
    const itemCategoryColor = theme === 'dark' ? 'text-subtleText' : 'text-slate-500';
    const cardBgColor = theme === 'dark' ? 'bg-dark-200' : 'bg-slate-100';
    const cardRingColor = theme === 'dark' ? 'ring-dark-300' : 'ring-slate-300';

    // Button variants based on theme
    const moveButtonVariant = theme === 'dark' ? 'buttonBlue' : 'primary_light';
    const removeButtonVariant = theme === 'dark' ? 'ghost' : 'outline';

    return (
        <div
            className={cn(
                `flex items-center justify-between p-3 rounded-lg shadow-sm ring-1 ${cardBgColor} ${cardRingColor}`
            )}
        >
            <div className="flex-grow min-w-0 mr-3">
                <p className={`font-medium truncate ${itemNameColor}`}>{spiral.name}</p>
                {spiral.category && (
                    <p className={`text-xs italic ${itemCategoryColor}`}>{spiral.category}</p>
                )}
            </div>

            <div className="flex items-center space-x-2 flex-shrink-0">
                <Button
                    onClick={() => onMoveSpiralToTasks(spiral.id)}
                    size="sm"
                    variant={moveButtonVariant}
                    className="px-3 py-1 text-xs h-auto"
                    aria-label={`Move ${spiral.name} to tasks`}
                >
                    <ArrowRightCircle className="h-4 w-4 mr-1" /> Move
                </Button>
                <Button
                    onClick={() => onRemoveSpiral(spiral.id)}
                    size="sm"
                    variant={removeButtonVariant}
                    className={cn("px-2 py-1 text-xs h-auto", theme === 'light' && "text-red-500 border-red-300 hover:bg-red-500/10 hover:border-red-500")}
                    aria-label={`Remove ${spiral.name}`}
                >
                    <Trash2 className="h-4 w-4" />
                </Button>
            </div>
        </div>
    );
};

export default SpiralItem;
