import React from 'react';
import { Button } from './ui/Button';
import { Badge } from './ui/badge';
import { Play, Pause, Trash2, CheckSquare } from 'lucide-react';

function TaskItem({ task, onRemoveTask, onStartTask, activeTaskOriginalId, isTimerActive, isBreakTime, timeRemaining }) {
    const isActiveCore = task.id === activeTaskOriginalId && isTimerActive && !isBreakTime;
    const isRunning = isActiveCore && timeRemaining > 0;
    const isPaused = isActiveCore && timeRemaining > 0 && !isTimerActive; // This logic might need review, usually !isTimerActive means paused if it IS the active task

    // More robust pause check based on App.js logic
    const isEffectivelyPaused = task.id === activeTaskOriginalId && !isTimerActive && timeRemaining > 0 && task.started && !task.completed && !isBreakTime;

    return (
        <li
            className={`p-3 rounded-md shadow flex items-center justify-between transition-all duration-200 ease-in-out ring-1 
                ${isRunning ? 'bg-dark-300 ring-cyanAccent/80' : 'bg-dark-200 ring-dark-300'}
                ${task.completed ? 'opacity-60 bg-dark-100 line-through' : 'hover:shadow-lg hover:ring-cyanAccent/50'}
            `}
        >
            <div className="flex items-center flex-grow min-w-0 mr-2">
                <span
                    className={`text-lightText truncate ${task.completed ? 'text-subtleText' : ''}`}
                    title={task.name}
                >
                    {task.name}
                </span>
                <span className="ml-2 text-xs text-subtleText whitespace-nowrap">
                    ({task.estimatedDuration} min)
                </span>
            </div>

            <div className="flex items-center space-x-2 flex-shrink-0">
                {task.completed ? (
                    <Badge variant="success" className="py-1 px-2">Done!</Badge>
                ) : isRunning ? (
                    <Badge variant="active" className="py-1 px-2">Active</Badge>
                ) : isEffectivelyPaused ? (
                    <Badge variant="warning" className="py-1 px-2">Paused</Badge>
                ) : null}

                {!task.completed && !isRunning && !isEffectivelyPaused && (
                    <Button
                        variant="outline"
                        size="sm"
                        onClick={() => onStartTask(task.id)}
                        aria-label={`Start ${task.name}`}
                        className="hover:bg-green-500/10 hover:text-green-400"
                    >
                        <Play className="h-4 w-4" />
                    </Button>
                )}
                {isEffectivelyPaused && (
                    <Button
                        variant="outline"
                        size="sm"
                        onClick={() => onStartTask(task.id)} // This should trigger resume logic in App.js if task.id is current
                        aria-label={`Resume ${task.name}`}
                        className="hover:bg-green-500/10 hover:text-green-400"
                    >
                        <Play className="h-4 w-4 mr-1" /> Resume
                    </Button>
                )}
                <Button
                    variant="destructive"
                    size="sm"
                    onClick={() => onRemoveTask(task.id)}
                    aria-label={`Remove ${task.name}`}
                >
                    <Trash2 className="h-4 w-4" />
                </Button>
            </div>
        </li>
    );
}

export default TaskItem;
