                  {/* Default Task Duration Setting */}
                  <div className="bg-dark-300/25 rounded-md p-3">
                    <div className="flex justify-between items-center mb-3">
                      <div className="flex items-center">
                        <Timer className="h-4 w-4 mr-1.5 text-cyanAccent" />
                        <span className="font-medium text-xs">Default Break Duration</span>
                      </div>
                      <div className="bg-dark-200/80 text-subtleText rounded px-2 py-0.5 text-[10px] flex items-center">
                        {DEFAULT_BREAK_DURATION_MINUTES} minutes
                      </div>
                    </div>
                    <input
                      type="range"
                      min="1"
                      max="15"
                      defaultValue={DEFAULT_BREAK_DURATION_MINUTES}
                      className="w-full h-1.5 bg-dark-300 rounded-lg appearance-none cursor-pointer"
                      disabled
                    />
                    <div className="text-[10px] text-subtleText/80 mt-1">Customizable break duration coming soon</div>
                  </div>

                  {/* Points System Setting */}
                  <div className="bg-dark-300/25 rounded-md p-3">
                    <div className="flex justify-between items-center">
                      <div className="flex items-center">
                        <Award className="h-4 w-4 mr-1.5 text-cyanAccent" />
                        <span className="font-medium text-xs">Points System</span>
                      </div>
                      <div className="bg-dark-200/80 text-subtleText rounded px-2 py-0.5 text-[10px] flex items-center">
                        Enabled
                      </div>
                    </div>
                    <div className="grid grid-cols-2 gap-1.5 mt-2 text-[10px]">
                      <div className="bg-dark-200/40 rounded p-1.5 flex justify-between items-center">
                        <span>Per Task:</span>
                        <span className="text-cyanAccent">{POINTS_PER_TASK} pts</span>
                      </div>
                      <div className="bg-dark-200/40 rounded p-1.5 flex justify-between items-center">
                        <span>Time Bonus:</span>
                        <span className="text-cyanAccent">{BONUS_POINTS_FACTOR} pts/s</span>
                      </div>
                      <div className="bg-dark-200/40 rounded p-1.5 flex justify-between items-center">
                        <span>Extension Penalty:</span>
                        <span className="text-cyanAccent">-{POINTS_DEDUCTION_FOR_EXTENSION} pts</span>
                      </div>
                    </div>
                  </div>

                  {/* About Section */}
                  <div className="bg-dark-300/25 rounded-md p-3">
                    <div className="flex items-center mb-2">
                      <BrainCircuit className="h-4 w-4 mr-1.5 text-cyanAccent" />
                      <span className="font-medium text-xs">About</span>
                    </div>
                    <div className="text-[10px] text-subtleText/80">
                      Annoying Pomodoro v0.1.0<br />
                      An annoyingly effective time management app
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          )}
        </main>
      </div>

      <Toaster />
      <PromptDialog 
        isOpen={isPromptOpen}
        onOpenChange={setIsPromptOpen}
        title={promptConfig.title}
        message={promptConfig.message}
        inputLabel={promptConfig.inputLabel}
        defaultValue={promptConfig.defaultValue}
        confirmText={promptConfig.confirmText}
        cancelText={promptConfig.cancelText}
        onConfirm={promptConfig.onConfirm}
        placeholder={promptConfig.placeholder}
      />
    </div>
  );
}

export default App;
