import React from 'react';
import { Button } from './ui/Button';
import { Play, Trash2, CheckCircle, Clock, AlertCircle, Pause } from 'lucide-react';

function TaskItem({ task, onRemoveTask, onStartTask, currentTaskIndex, activeTaskOriginalId, isTimerActive, isBreakTime, timeRemaining }) {
    const isActiveCore = task.id === activeTaskOriginalId && !isBreakTime && !task.completed;
    const isRunning = isActiveCore && isTimerActive;
    const isPaused = isActiveCore && !isTimerActive && task.started && timeRemaining > 0;

    // Calculate progress percentage if task is active
    const progressPercentage = isActiveCore && task.estimatedDuration > 0
        ? ((task.estimatedDuration * 60 - timeRemaining) / (task.estimatedDuration * 60)) * 100
        : 0;

    // Base container styles
    let containerClasses = "relative p-4 rounded-lg shadow-sm transition-all duration-300 ease-in-out group";

    // Status-specific styling
    if (task.completed) {
        containerClasses += " bg-dark-200/50 opacity-80";
    } else if (isRunning) {
        containerClasses += " bg-gradient-to-r from-dark-200 to-dark-300/80 border-l-4 border-cyanAccent ring-1 ring-cyanAccent/30";
    } else if (isPaused) {
        containerClasses += " bg-gradient-to-r from-dark-200 to-dark-200/90 border-l-4 border-accent-yellow ring-1 ring-accent-yellow/30";
    } else {
        containerClasses += " bg-dark-200/80 hover:bg-dark-200/90 ring-1 ring-dark-300/40 hover:ring-cyanAccent/50";
    }

    return (
        <li className={containerClasses}>
            {/* Progress bar for running tasks */}
            {isActiveCore && (
                <div className="absolute bottom-0 left-0 h-1 bg-cyanAccent/20 rounded-b-lg" style={{ width: `100%` }}>
                    <div
                        className={`h-full rounded-b-lg ${isRunning ? 'bg-cyanAccent' : 'bg-accent-yellow'}`}
                        style={{ width: `${progressPercentage}%` }}
                    ></div>
                </div>
            )}

            <div className="flex items-start justify-between gap-3">
                <div className={`flex-grow flex items-start min-w-0 ${task.completed ? 'text-subtleText' : 'text-lightText'}`}>
                    {task.completed ? (
                        <CheckCircle className="h-5 w-5 mt-0.5 mr-3 text-accent-green flex-shrink-0" />
                    ) : isRunning ? (
                        <div className="relative mr-3 flex-shrink-0">
                            <div className="h-5 w-5 rounded-full bg-cyanAccent/20 animate-pulse"></div>
                            <div className="absolute inset-0 flex items-center justify-center">
                                <Clock className="h-3.5 w-3.5 text-cyanAccent" />
                            </div>
                        </div>
                    ) : isPaused ? (
                        <div className="relative mr-3 flex-shrink-0">
                            <Pause className="h-5 w-5 text-accent-yellow" />
                        </div>
                    ) : (
                        <Clock className="h-5 w-5 mt-0.5 mr-3 text-subtleText/70 group-hover:text-cyanAccent/70 transition-colors duration-300 flex-shrink-0" />
                    )}

                    <div className="flex flex-col min-w-0 py-0.5">
                        <span className={`relative font-medium ${task.completed ? 'line-through decoration-slate-500 decoration-2 text-subtleText' : ''}`}>
                            {task.name}
                        </span>

                        <div className="flex items-center text-xs text-subtleText mt-1">
                            <div className={`flex items-center ${task.completed ? 'text-slate-500' : ''}`}>
                                <Clock className="h-3 w-3 mr-1 opacity-70" />
                                {task.estimatedDuration} min
                            </div>

                            {isRunning && (
                                <span className="flex items-center ml-3 text-cyanAccent text-xs">
                                    <span className="h-1.5 w-1.5 rounded-full bg-cyanAccent mr-1 animate-pulse"></span>
                                    In progress
                                </span>
                            )}

                            {isPaused && (
                                <span className="flex items-center ml-3 text-accent-yellow text-xs">
                                    <AlertCircle className="h-3 w-3 mr-1" />
                                    Paused
                                </span>
                            )}
                        </div>
                    </div>
                </div>

                <div className="flex items-center space-x-2 flex-shrink-0">
                    {!task.completed && !isRunning && !isPaused && (
                        <Button
                            variant="buttonGreen"
                            size="sm"
                            onClick={() => onStartTask(task.id)}
                            disabled={isTimerActive}
                            aria-label={`Start task ${task.name}`}
                            className="shadow-sm"
                            leftIcon={<Play className="h-3.5 w-3.5" />}
                        >
                            Start
                        </Button>
                    )}

                    <Button
                        variant={task.completed ? "ghost" : "icon"}
                        size="iconXs"
                        onClick={() => onRemoveTask(task.id)}
                        aria-label={`Remove task ${task.name}`}
                        className={`hover:text-buttonRed ${task.completed ? 'opacity-70 hover:opacity-100' : ''}`}
                    >
                        <Trash2 className="h-3.5 w-3.5" />
                    </Button>
                </div>
            </div>
        </li>
    );
}

export default TaskItem;
