import React, { useState, useEffect, useCallback } from 'react';
import { Button } from './components/ui/Button';
import { Card, CardHeader, CardTitle, CardContent } from './components/ui/Card';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import { Toaster } from './components/Toaster';
import { toast } from './hooks/use-toast';

const motivationalQuotes = [
  "You won\'t be able to do it... Prove me wrong!",
  "Don\'t disappoint future you.",
  "Less scrolling, more doing!",
  "That task isn\'t going to complete itself.",
  "Are you a talker or a doer?",
  "Time is ticking. Are you?",
  "Stop procrastinating. Start dominating.",
  "Is this the best use of your time right now?",
  "The clock is your boss. Don\'t get fired."
];

function App() {
  const [naggingQuote, setNaggingQuote] = useState("");
  const [tasks, setTasks] = useState([]);
  const [currentTaskIndex, setCurrentTaskIndex] = useState(-1);
  const [isTimerActive, setIsTimerActive] = useState(false);

  const [timeRemaining, setTimeRemaining] = useState(0);
  const [timerIntervalId, setTimerIntervalId] = useState(null);
  const [isBreakTime, setIsBreakTime] = useState(false);

  const [score, setScore] = useState(0);
  const pointsPerTask = 10;
  const bonusPointsFactor = 0.5;

  const [spirals, setSpirals] = useState([]);

  const notificationSound = React.useRef(null);

  useEffect(() => {
    const getRandomQuote = () => motivationalQuotes[Math.floor(Math.random() * motivationalQuotes.length)];
    setNaggingQuote(getRandomQuote());
    notificationSound.current = new Audio('/assets/notification.mp3');

    if (Notification.permission !== "granted" && Notification.permission !== "denied") {
        Notification.requestPermission().then(permission => {
            if (permission === "granted") {
                console.log("Desktop notification permission granted.");
            }
        });
    }
  }, []);

  const playNotificationSound = () => {
    notificationSound.current?.play().catch(error => console.error("Error playing sound:", error));
  };

  const showDesktopNotification = (title, body) => {
    if (Notification.permission === "granted") {
        new Notification(title, { body });
    }
  };

  const handleAddTask = useCallback((newTaskData) => {
    setTasks(prevTasks => [
      ...prevTasks,
      {
        id: Date.now(),
        name: newTaskData.name,
        duration: newTaskData.duration,
        estimatedDuration: newTaskData.duration,
        completed: false,
        started: false,
        timeSpentSeconds: 0,
        timerStartTime: null,
        completionTimestamp: null,
      }
    ]);
    toast({ title: "Task Added", description: `"${newTaskData.name}" added to your list.` });
  }, []);

  const handleRemoveTask = useCallback((taskId) => {
    const taskToRemove = tasks.find(t => t.id === taskId);
    setTasks(prevTasks => prevTasks.filter(task => task.id !== taskId));
    if (taskToRemove) {
        toast({ title: "Task Removed", description: `"${taskToRemove.name}" has been removed.`, variant: "destructive" });
    }
    if (currentTaskIndex !== -1 && tasks[currentTaskIndex]?.id === taskId) {
        if (timerIntervalId) clearInterval(timerIntervalId);
        setTimerIntervalId(null);
        setCurrentTaskIndex(-1);
        setTimeRemaining(0);
        setIsTimerActive(false);
        setIsBreakTime(false); 
    }
  }, [tasks, currentTaskIndex, timerIntervalId]);

  const handleStartTimerForTask = useCallback((taskId) => {
    if (isTimerActive && !isBreakTime) {
        toast({ title: "Timer Active", description: "Another task is already running.", variant: "destructive" });
        return;
    }
    if (isBreakTime) {
        if (timerIntervalId) clearInterval(timerIntervalId);
        setTimerIntervalId(null);
        setIsBreakTime(false);
        toast({title: "Break Interrupted", description: "Task started, break ended."}) 
    }

    const taskIndex = tasks.findIndex(task => task.id === taskId);
    if (taskIndex !== -1) {
        const taskToStart = tasks[taskIndex];
        setTasks(prevTasks => 
            prevTasks.map((task, index) => 
                index === taskIndex ? { ...task, started: true, timerStartTime: Date.now() } : task
            )
        );
        setCurrentTaskIndex(taskIndex);
        setIsTimerActive(true);
        console.log(`Starting task: ${tasks[taskIndex].name}`);
    }
  }, [tasks, isTimerActive]);

  const calculateDailyStats = () => {
    const totalDuration = tasks.reduce((acc, task) => acc + task.estimatedDuration, 0);
    const remainingTaskMinutes = tasks.filter(task => !task.completed && !task.started).reduce((acc, task) => acc + task.estimatedDuration, 0);
    return {
      totalPlannedTime: totalDuration,
      remainingTaskTime: remainingTaskMinutes,
      probNotFinishing: 0
    };
  };

  const dailyStats = calculateDailyStats();
  const currentTaskName = currentTaskIndex !== -1 && tasks[currentTaskIndex] ? tasks[currentTaskIndex].name : "None";

  return (
    <div className="min-h-screen bg-dark text-lightText p-4 sm:p-6 md:p-8">
      <header className="bg-dark-100 p-6 rounded-lg shadow-lg mb-8 ring-1 ring-dark-300">
        <h1 className="text-4xl font-bold text-center text-cyanAccent">Time Boxer</h1>
        {naggingQuote && <p className="text-center text-subtleText italic mt-2">{naggingQuote}</p>}
      </header>

      <main className="max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-3 gap-6">
        <Card className="lg:col-span-3 bg-dark-100 ring-1 ring-dark-300">
          <CardHeader className="text-center">
            <CardTitle className="text-2xl font-semibold text-cyanAccent">Current Task: {currentTaskName}</CardTitle>
          </CardHeader>
          <CardContent className="text-center">
            <div id="timer-clock-react" className="text-8xl font-bold text-timerAccent my-4">
              00:00
            </div>
            <div className="space-x-2 mb-4">
              <Button variant="buttonBlue" size="lg" disabled={!tasks[currentTaskIndex] || !tasks[currentTaskIndex].started || isTimerActive}>Start/Resume</Button>
              <Button variant="buttonGray" size="lg" disabled={!isTimerActive}>Pause</Button>
              <Button variant="buttonGray" size="lg" disabled={!tasks[currentTaskIndex] || !tasks[currentTaskIndex].started } >Extend Time</Button>
            </div>
            <div>
              <Button variant="buttonGreen" size="lg" className="w-full max-w-xs mx-auto" disabled={!tasks[currentTaskIndex] || !tasks[currentTaskIndex].started}>Done!</Button>
            </div>
          </CardContent>
        </Card>

        <Card className="lg:col-span-1 bg-dark-100 ring-1 ring-dark-300">
          <CardHeader>
            <CardTitle className="text-xl font-semibold text-cyanAccent">Plan Your Day</CardTitle>
          </CardHeader>
          <CardContent>
            <TaskForm onAddTask={handleAddTask} />
            <div className="mt-6 pt-6 border-t border-dark-300 space-y-2">
                <h3 className="text-md font-semibold text-cyanAccent mb-3">Daily Stats</h3>
                <p className="text-sm text-subtleText flex justify-between">Total Planned: <span className='text-timerAccent font-semibold'>{dailyStats.totalPlannedTime} min</span></p>
                <p className="text-sm text-subtleText flex justify-between">Remaining: <span className='text-timerAccent font-semibold'>{dailyStats.remainingTaskTime} min</span></p>
                <p className="text-sm text-subtleText flex justify-between">P(Not Finishing): <span className='text-timerAccent font-semibold'>{dailyStats.probNotFinishing}%</span></p>
            </div>
          </CardContent>
        </Card>

        <Card className="lg:col-span-2 bg-dark-100 ring-1 ring-dark-300">
          <CardHeader>
            <CardTitle className="text-xl font-semibold text-cyanAccent">Today's Tasks</CardTitle>
          </CardHeader>
          <CardContent>
            <TaskList 
                tasks={tasks} 
                onRemoveTask={handleRemoveTask} 
                onStartTask={handleStartTask}
                isTimerActive={isTimerActive} 
            />
          </CardContent>
        </Card>

        <Card className="lg:col-span-3 bg-dark-100 ring-1 ring-dark-300">
          <CardHeader>
            <CardTitle className="text-xl font-semibold text-cyanAccent">Spirals (For Later)</CardTitle>
