import React from 'react';
import { Button } from './ui/Button';
import { Play, Trash2, CheckCircle } from 'lucide-react'; // Added CheckCircle for completed visual

function TaskItem({ task, onRemoveTask, onStartTask, currentTaskIndex, activeTaskOriginalId, isTimerActive, isBreakTime }) {
    const isActive = task.id === activeTaskOriginalId && isTimerActive && !isBreakTime && !task.completed;
    return (
        <li
            className={`p-3 bg-dark-200 rounded-md shadow flex justify-between items-center ring-1 ring-dark-300 transition-all duration-200 ease-in-out hover:shadow-lg hover:ring-cyanAccent/50 
                        ${isActive ? 'ring-2 ring-cyanAccent scale-[1.01]' : ''}
                        ${task.completed ? 'opacity-60' : ''}`}
        >
            <span className={`flex-grow flex items-center ${task.completed ? 'text-subtleText' : 'text-lightText'}`}>
                {task.completed && <CheckCircle className="h-5 w-5 mr-2 text-green-500 flex-shrink-0" />}
                <span className={`relative ${task.completed ? 'line-through decoration-slate-500 decoration-2' : ''}`}>
                    {task.name}
                </span>
                <span className={`ml-2 text-xs ${task.completed ? 'text-slate-500 line-through decoration-slate-500 decoration-2' : 'text-muted-foreground'}`}>({task.estimatedDuration} min)</span>
            </span>
            <div className="flex items-center space-x-2 flex-shrink-0 ml-2">
                {!task.completed && !isActive && (
                    <Button
                        variant="buttonGreen"
                        size="sm"
                        onClick={() => onStartTask(task.id)}
                        disabled={isTimerActive && !isActive} // Disable if any timer is active UNLESS it's this task trying to resume (covered by isActive)
                        aria-label={`Start task ${task.name}`}
                    >
                        <Play className="h-4 w-4 mr-1" /> Start
                    </Button>
                )}
                {isActive && (
                    <span className="text-xs text-cyanAccent font-semibold px-2 py-1 bg-cyanAccent/10 rounded-full">Active</span>
                )}
                {task.completed && (
                    <span className="text-xs text-green-400 font-semibold px-2 py-1 bg-green-400/10 rounded-full">Done!</span>
                )}
                <Button
                    variant="destructive"
                    size="sm"
                    onClick={() => onRemoveTask(task.id)}
                    aria-label={`Remove task ${task.name}`}
                >
                    <Trash2 className="h-4 w-4" />
                </Button>
            </div>
        </li>
    );
}

export default TaskItem;
