import React from 'react';
import Navigation from './Navigation';
import { Toaster } from './Toaster';

const Layout = ({
    children,
    activeView,
    onChangeView,
    score,
    motivationalQuote
}) => {
    return (
        <div className="min-h-screen bg-dark-500 text-lightText pb-10">
            <div className="container-responsive mx-auto pt-5">
                {/* Main navigation */}
                <Navigation
                    activeView={activeView}
                    onChangeView={onChangeView}
                    score={score}
                />

                {/* Motivational quote banner */}
                {motivationalQuote && (
                    <div className="mb-6 py-3 px-5 bg-dark-100 rounded-lg text-center animate-fade-in">
                        <p className="text-subtleText italic text-sm">{motivationalQuote}</p>
                    </div>
                )}

                {/* Main content */}
                <div className="animate-slide-up">
                    {children}
                </div>

                {/* Toast notifications */}
                <Toaster />
            </div>
        </div>
    );
};

export default Layout;
