import React from 'react';
import SpiralItem from './SpiralItem';

const SpiralList = ({
    spirals,
    onRemoveSpiral,
    onMoveSpiralToTasks,
    theme
}) => {
    if (!spirals || spirals.length === 0) {
        return <p className={`text-center ${theme === 'dark' ? 'text-subtleText' : 'text-slate-500'} mt-4`}>No spirals (ideas) yet. Add some!</p>;
    }

    return (
        <div className="space-y-3 overflow-y-auto h-full pr-1">
            {spirals.map((spiral) => (
                <SpiralItem
                    key={spiral.id}
                    spiral={spiral}
                    onRemoveSpiral={onRemoveSpiral}
                    onMoveSpiralToTasks={onMoveSpiralToTasks}
                    theme={theme}
                />
            ))}
        </div>
    );
};

export default SpiralList;
