import React, { useState, useEffect } from 'react';
import { getStatsHistory, exportStatsHistory, importStatsHistory } from '../lib/statsHistory';
import {
    BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
    LineChart, Line, AreaChart, Area
} from 'recharts';
import { Card, CardHeader, CardTitle, CardContent } from './ui/Card';
import { Button } from './ui/Button';
import { Download, Upload, Calendar, Clock, Activity, TrendingUp } from 'lucide-react';

const DashboardView = () => {
    const [statsHistory, setStatsHistory] = useState([]);
    const [timeRange, setTimeRange] = useState(7); // Default to 7 days
    const [chartType, setChartType] = useState('bar'); // 'bar', 'line', or 'area'

    useEffect(() => {
        // Load stats history when component mounts or timeRange changes
        const history = getStatsHistory(timeRange);

        // Reverse array so dates appear in chronological order (oldest to newest)
        setStatsHistory([...history].reverse());
    }, [timeRange]);

    const handleExport = () => {
        // Create a blob with the stats history data
        const jsonData = exportStatsHistory();
        const blob = new Blob([jsonData], { type: 'application/json' });
        const url = URL.createObjectURL(blob);

        // Create a temporary link and trigger download
        const a = document.createElement('a');
        a.href = url;
        a.download = `annoying-pomodoro-stats-${new Date().toISOString().split('T')[0]}.json`;
        document.body.appendChild(a);
        a.click();

        // Clean up
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
    };

    const handleImport = () => {
        // Create file input element
        const input = document.createElement('input');
        input.type = 'file';
        input.accept = '.json';

        input.onchange = (e) => {
            const file = e.target.files[0];
            if (!file) return;

            const reader = new FileReader();
            reader.onload = (event) => {
                const jsonData = event.target.result;
                if (importStatsHistory(jsonData)) {
                    // Refresh data after successful import
                    const history = getStatsHistory(timeRange);
                    setStatsHistory([...history].reverse());
                } else {
                    alert('Failed to import stats. Invalid format.');
                }
            };
            reader.readAsText(file);
        };

        input.click();
    };

    // Format data for better display
    const formatMinutes = (totalSeconds) => {
        if (isNaN(totalSeconds) || totalSeconds < 0) return "0m";
        const hours = Math.floor(totalSeconds / 3600);
        const minutes = Math.floor((totalSeconds % 3600) / 60);

        if (hours > 0) {
            return `${hours}h ${minutes}m`;
        }
        return `${minutes}m`;
    };

    // Custom tooltip component for charts
    const CustomTooltip = ({ active, payload, label }) => {
        if (active && payload && payload.length) {
            return (
                <div className="bg-white dark:bg-gray-800 p-3 border border-gray-200 dark:border-gray-700 rounded shadow-md">
                    <p className="font-bold">{label}</p>
                    {payload.map((entry, index) => (
                        <p key={index} style={{ color: entry.color }}>
                            {entry.name}: {formatMinutes(entry.value)}
                        </p>
                    ))}
                </div>
            );
        }
        return null;
    };

    // No data state
    if (statsHistory.length === 0) {
        return (
            <div className="flex flex-col items-center justify-center h-full p-4">
                <Card className="w-full max-w-3xl">
                    <CardHeader>
                        <CardTitle>Productivity Dashboard</CardTitle>
                    </CardHeader>
                    <CardContent>
                        <div className="flex flex-col items-center justify-center py-8">
                            <Activity className="w-12 h-12 text-gray-400 mb-4" />
                            <p className="text-lg text-center mb-4">No historical data available yet</p>
                            <p className="text-sm text-center text-gray-500 mb-6">
                                Complete tasks to start building your productivity history
                            </p>
                            <div className="flex space-x-4">
                                <Button onClick={handleImport} className="flex items-center">
                                    <Upload className="w-4 h-4 mr-2" />
                                    Import Data
                                </Button>
                            </div>
                        </div>
                    </CardContent>
                </Card>
            </div>
        );
    }

    return (
        <div className="p-4 space-y-4">
            <Card className="bg-gradient-to-br from-dark-100/90 to-dark-200/90 ring-1 ring-dark-300/50 shadow-md hover:shadow-lg transition-all duration-500">
                <CardHeader className="flex flex-row items-center justify-between py-3 border-b border-dark-300/25">
                    <CardTitle className="text-lg font-semibold text-transparent bg-clip-text bg-gradient-to-r from-cyanAccent to-brightAccent">Productivity Dashboard</CardTitle>
                    <div className="flex space-x-2">
                        <Button
                            variant={timeRange === 7 ? 'default' : 'outline'}
                            size="sm"
                            onClick={() => setTimeRange(7)}
                            className={`${timeRange === 7 ? 'ring-1 ring-cyanAccent/60' : 'hover:bg-dark-200/40 hover:ring-1 hover:ring-dark-300/60'}`}
                        >
                            7 Days
                        </Button>
                        <Button
                            variant={timeRange === 14 ? 'default' : 'outline'}
                            size="sm"
                            onClick={() => setTimeRange(14)}
                            className={`${timeRange === 14 ? 'ring-1 ring-cyanAccent/60' : 'hover:bg-dark-200/40 hover:ring-1 hover:ring-dark-300/60'}`}
                        >
                            14 Days
                        </Button>
                        <Button
                            variant={timeRange === 30 ? 'default' : 'outline'}
                            size="sm"
                            onClick={() => setTimeRange(30)}
                            className={`${timeRange === 30 ? 'ring-1 ring-cyanAccent/60' : 'hover:bg-dark-200/40 hover:ring-1 hover:ring-dark-300/60'}`}>
                            30 Days
                        </Button>
                    </div>
                </CardHeader>
                <CardContent className="p-3.5">
                    <div className="mb-4 flex justify-between items-center">
                        <div className="flex space-x-2">
                            <Button
                                variant={chartType === 'bar' ? 'default' : 'outline'}
                                size="sm"
                                onClick={() => setChartType('bar')}
                                className={`${chartType === 'bar' ? 'ring-1 ring-cyanAccent/60' : 'hover:bg-dark-200/40 hover:ring-1 hover:ring-dark-300/60'}`}>
                                Bar
                            </Button>
                            <Button
                                variant={chartType === 'line' ? 'default' : 'outline'}
                                size="sm"
                                onClick={() => setChartType('line')}
                                className={`${chartType === 'line' ? 'ring-1 ring-cyanAccent/60' : 'hover:bg-dark-200/40 hover:ring-1 hover:ring-dark-300/60'}`}>
                                Line
                            </Button>
                            <Button
                                variant={chartType === 'area' ? 'default' : 'outline'}
                                size="sm"
                                onClick={() => setChartType('area')}
                                className={`${chartType === 'area' ? 'ring-1 ring-cyanAccent/60' : 'hover:bg-dark-200/40 hover:ring-1 hover:ring-dark-300/60'}`}>
                                Area
                            </Button>
                        </div>
                        <div className="flex space-x-2">
                            <Button onClick={handleExport} size="sm" variant="outline" className="flex items-center hover:bg-dark-200/40 hover:ring-1 hover:ring-dark-300/60">
                                <Download className="w-4 h-4 mr-2" />
                                Export
                            </Button>
                            <Button onClick={handleImport} size="sm" variant="outline" className="flex items-center hover:bg-dark-200/40 hover:ring-1 hover:ring-dark-300/60">
                                <Upload className="w-4 h-4 mr-2" />
                                Import
                            </Button>
                        </div>
                    </div>

                    <div className="h-80 w-full">
                        <ResponsiveContainer width="100%" height="100%">
                            {chartType === 'bar' && (
                                <BarChart data={statsHistory}>
                                    <CartesianGrid strokeDasharray="3 3" stroke="#4A5568" />
                                    <XAxis dataKey="date" stroke="#A0AEC0" />
                                    <YAxis tickFormatter={(value) => formatMinutes(value)} stroke="#A0AEC0" />
                                    <Tooltip content={<CustomTooltip />} cursor={{ fill: 'rgba(100, 116, 139, 0.1)' }} />
                                    <Legend wrapperStyle={{ color: '#A0AEC0' }} />
                                    <Bar dataKey="focusTime" name="Focus Time" fill="#6366F1" />
                                    <Bar dataKey="idleTime" name="Idle Time" fill="#F97316" />
                                </BarChart>
                            )}

                            {chartType === 'line' && (
                                <LineChart data={statsHistory}>
                                    <CartesianGrid strokeDasharray="3 3" stroke="#4A5568" />
                                    <XAxis dataKey="date" stroke="#A0AEC0" />
                                    <YAxis tickFormatter={(value) => formatMinutes(value)} stroke="#A0AEC0" />
                                    <Tooltip content={<CustomTooltip />} cursor={{ fill: 'rgba(100, 116, 139, 0.1)' }} />
                                    <Legend wrapperStyle={{ color: '#A0AEC0' }} />
                                    <Line type="monotone" dataKey="focusTime" name="Focus Time" stroke="#6366F1" strokeWidth={2} dot={{ r: 3 }} activeDot={{ r: 5 }} />
                                    <Line type="monotone" dataKey="idleTime" name="Idle Time" stroke="#F97316" strokeWidth={2} dot={{ r: 3 }} activeDot={{ r: 5 }} />
                                </LineChart>
                            )}

                            {chartType === 'area' && (
                                <AreaChart data={statsHistory}>
                                    <CartesianGrid strokeDasharray="3 3" stroke="#4A5568" />
                                    <XAxis dataKey="date" stroke="#A0AEC0" />
                                    <YAxis tickFormatter={(value) => formatMinutes(value)} stroke="#A0AEC0" />
                                    <Tooltip content={<CustomTooltip />} cursor={{ fill: 'rgba(100, 116, 139, 0.1)' }} />
                                    <Legend wrapperStyle={{ color: '#A0AEC0' }} />
                                    <Area type="monotone" dataKey="focusTime" name="Focus Time" stroke="#6366F1" fill="#6366F1" fillOpacity={0.3} />
                                    <Area type="monotone" dataKey="idleTime" name="Idle Time" stroke="#F97316" fill="#F97316" fillOpacity={0.3} />
                                </AreaChart>
                            )}
                        </ResponsiveContainer>
                    </div>
                </CardContent>
            </Card>

            {/* Stats summary cards */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <Card className="bg-gradient-to-br from-dark-100/90 to-dark-200/90 ring-1 ring-dark-300/50 shadow-md hover:shadow-lg transition-all duration-500">
                    <CardContent className="pt-6 flex items-center">
                        <Calendar className="h-8 w-8 text-blue-500 mr-4" />
                        <div>
                            <p className="text-sm font-medium text-subtleText">Total Days Tracked</p>
                            <h3 className="text-2xl font-bold text-timerAccent">{statsHistory.length}</h3>
                        </div>
                    </CardContent>
                </Card>

                <Card className="bg-gradient-to-br from-dark-100/90 to-dark-200/90 ring-1 ring-dark-300/50 shadow-md hover:shadow-lg transition-all duration-500">
                    <CardContent className="pt-6 flex items-center">
                        <Clock className="h-8 w-8 text-indigo-500 mr-4" />
                        <div>
                            <p className="text-sm font-medium text-subtleText">Total Focus Time</p>
                            <h3 className="text-2xl font-bold text-timerAccent">
                                {formatMinutes(statsHistory.reduce((acc, day) => acc + (day.focusTime || 0), 0))}
                            </h3>
                        </div>
                    </CardContent>
                </Card>

                <Card className="bg-gradient-to-br from-dark-100/90 to-dark-200/90 ring-1 ring-dark-300/50 shadow-md hover:shadow-lg transition-all duration-500">
                    <CardContent className="pt-6 flex items-center">
                        <TrendingUp className="h-8 w-8 text-green-500 mr-4" />
                        <div>
                            <p className="text-sm font-medium text-subtleText">Total Score</p>
                            <h3 className="text-2xl font-bold text-timerAccent">
                                {statsHistory.reduce((acc, day) => acc + (day.score || 0), 0).toLocaleString()}
                            </h3>
                        </div>
                    </CardContent>
                </Card>
            </div>

            {/* Detailed stats table - already styled */}
            <Card className="bg-gradient-to-br from-dark-100/90 to-dark-200/90 ring-1 ring-dark-300/50 h-full flex flex-col shadow-md hover:shadow-lg transition-all duration-500">
                <CardHeader className="py-3 border-b border-dark-300/25">
                    <CardTitle className="text-lg font-semibold text-transparent bg-clip-text bg-gradient-to-r from-cyanAccent to-brightAccent">Detailed Stats</CardTitle>
                </CardHeader>
                <CardContent className="flex-grow overflow-hidden p-3.5">
                    <div className="overflow-x-auto">
                        <table className="w-full text-sm">
                            <thead>
                                <tr className="border-b border-dark-300/40">
                                    <th className="py-2 px-4 text-left text-subtleText">Date</th>
                                    <th className="py-2 px-4 text-left text-subtleText">Focus Time</th>
                                    <th className="py-2 px-4 text-left text-subtleText">Idle Time</th>
                                    <th className="py-2 px-4 text-left text-subtleText">Tasks Completed</th>
                                    <th className="py-2 px-4 text-left text-subtleText">Score</th>
                                </tr>
                            </thead>
                            <tbody>
                                {statsHistory.map((day, index) => (
                                    <tr key={index} className={`${index % 2 === 0 ? "bg-dark-200/30" : "bg-dark-100/30"} hover:bg-dark-300/40 transition-colors`}>
                                        <td className="py-2 px-4 text-subtleText">{day.date}</td>
                                        <td className="py-2 px-4 text-timerAccent font-semibold">{formatMinutes(day.focusTime || 0)}</td>
                                        <td className="py-2 px-4 text-timerAccent font-semibold">{formatMinutes(day.idleTime || 0)}</td>
                                        <td className="py-2 px-4 text-timerAccent font-semibold">{day.tasksCompleted || 0}</td>
                                        <td className="py-2 px-4 text-timerAccent font-semibold">{day.score || 0}</td>
                                    </tr>
                                ))}
                                {statsHistory.length === 0 && (
                                    <tr className="bg-dark-100/30">
                                        <td colSpan="5" className="py-10 text-center text-subtleText italic">No historical data available yet. Complete some tasks!</td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </CardContent>
            </Card>
        </div>
    );
};

export default DashboardView;
