import React from 'react';
import TaskForm from './TaskForm';
import TaskList from './TaskList';
import { Card, CardContent, CardHeader, CardTitle } from './ui/Card';
import { Clock, Calendar, AlertCircle } from 'lucide-react';

const PlanView = ({
    tasks,
    onAddTask,
    onRemoveTask,
    onStartTask,
    currentTaskIndex,
    activeTaskOriginalId,
    isTimerActive,
    isBreakTime,
    timeRemaining,
    dailyStats
}) => {
    // Calculate total time planned
    const totalPlannedMinutes = tasks.reduce((total, task) => total + task.estimatedDuration, 0);

    // Calculate remaining time in the day (assuming 8 working hours)
    const workingMinutesInDay = 8 * 60; // 8 hours
    const remainingMinutes = workingMinutesInDay - totalPlannedMinutes;

    // Get a warning message if over-planned
    const getTimeWarning = () => {
        if (remainingMinutes < 0) {
            return `You're over-planned by ${Math.abs(remainingMinutes)} minutes. Consider adjusting your tasks.`;
        }
        if (remainingMinutes < 60) {
            return `You have only ${remainingMinutes} minutes remaining in your day.`;
        }
        return null;
    };

    const timeWarning = getTimeWarning();

    return (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            <div className="lg:col-span-1">
                {/* Task form */}
                <TaskForm onAddTask={onAddTask} />

                {/* Time planning stats */}
                <Card
                    variant="subtle"
                    className="mt-6"
                    borderStyle={remainingMinutes < 0 ? "accent" : "none"}
                >
                    <CardHeader className="pb-2">
                        <CardTitle className="flex items-center text-base">
                            <Calendar className="h-4 w-4 mr-2 text-accent-purple" />
                            Time Planning
                        </CardTitle>
                    </CardHeader>
                    <CardContent>
                        <div className="space-y-4">
                            <div className="flex justify-between items-center">
                                <div className="flex items-center text-subtleText">
                                    <Clock className="h-4 w-4 mr-1.5" />
                                    <span>Total planned time</span>
                                </div>
                                <div className="font-medium text-lightText">
                                    {totalPlannedMinutes} min
                                </div>
                            </div>

                            <div className="flex justify-between items-center">
                                <div className="flex items-center text-subtleText">
                                    <Clock className="h-4 w-4 mr-1.5" />
                                    <span>Remaining in day</span>
                                </div>
                                <div className={`font-medium ${remainingMinutes < 0 ? 'text-buttonRed' : 'text-lightText'}`}>
                                    {remainingMinutes} min
                                </div>
                            </div>

                            {timeWarning && (
                                <div className={`text-xs rounded-lg p-3 flex items-start ${remainingMinutes < 0 ? 'bg-buttonRed/10 text-buttonRed' : 'bg-accent-yellow/10 text-accent-yellow'}`}>
                                    <AlertCircle className="h-4 w-4 mr-1.5 flex-shrink-0 mt-0.5" />
                                    <span>{timeWarning}</span>
                                </div>
                            )}
                        </div>
                    </CardContent>
                </Card>
            </div>

            <div className="lg:col-span-2">
                {/* Task list */}
                <TaskList
                    tasks={tasks}
                    onRemoveTask={onRemoveTask}
                    onStartTask={onStartTask}
                    currentTaskIndex={currentTaskIndex}
                    activeTaskOriginalId={activeTaskOriginalId}
                    isTimerActive={isTimerActive}
                    isBreakTime={isBreakTime}
                    timeRemaining={timeRemaining}
                />
            </div>
        </div>
    );
};

export default PlanView;
