import React from 'react';
import { Button } from './ui/Button';
import { Play, Trash2 } from 'lucide-react'; // Icons for buttons

function TaskItem({ task, onRemoveTask, onStartTask, isTimerActive }) {
    return (
        <li className={`p-3 bg-dark-200 rounded-md shadow flex justify-between items-center ring-1 ring-dark-300 transition-all duration-200 ease-in-out hover:shadow-lg hover:ring-cyanAccent/50 ${task.started && !task.completed ? 'ring-2 ring-cyanAccent' : ''}`}>
            <span className={`flex-grow ${task.completed ? 'line-through text-subtleText opacity-70' : 'text-lightText'}`}>
                {task.name} <span className="text-xs text-muted-foreground">({task.estimatedDuration} min)</span>
            </span>
            <div className="flex items-center space-x-2 flex-shrink-0 ml-2">
                {!task.completed && !task.started && (
                    <Button
                        variant="buttonGreen"
                        size="sm"
                        onClick={() => onStartTask(task.id)}
                        disabled={isTimerActive} // Disable if any timer is already active
                        aria-label={`Start task ${task.name}`}
                    >
                        <Play className="h-4 w-4 mr-1" /> Start
                    </Button>
                )}
                {!task.completed && task.started && (
                    <span className="text-xs text-cyanAccent font-semibold px-2 py-1 bg-cyanAccent/10 rounded-full">Active</span>
                )}
                {task.completed && (
                    <span className="text-xs text-green-400 font-semibold px-2 py-1 bg-green-400/10 rounded-full">Done!</span>
                )}
                <Button
                    variant="destructive"
                    size="sm"
                    onClick={() => onRemoveTask(task.id)}
                    aria-label={`Remove task ${task.name}`}
                >
                    <Trash2 className="h-4 w-4" />
                </Button>
            </div>
        </li>
    );
}

export default TaskItem;
