import React from 'react';
import { Button } from './ui/Button';
import { Play, Pause, Trash2, CheckCircle } from 'lucide-react'; // Added Pause for Resume button

function TaskItem({ task, onRemoveTask, onStartTask, currentTaskIndex, activeTaskOriginalId, isTimerActive, isBreakTime, timeRemaining }) {
    const isActiveCore = task.id === activeTaskOriginalId && !isBreakTime && !task.completed;
    const isRunning = isActiveCore && isTimerActive;
    // A task is paused if it's the current one, timer isn't active, but still has time and was started.
    const isPaused = isActiveCore && !isTimerActive && task.started && timeRemaining > 0;

    return (
        <li
            className={`p-3 bg-dark-200 rounded-md shadow flex justify-between items-center ring-1 ring-dark-300 transition-all duration-200 ease-in-out hover:shadow-lg hover:ring-cyanAccent/50 
                        ${isRunning ? 'ring-2 ring-cyanAccent scale-[1.01]' : ''}
                        ${task.completed ? 'opacity-60' : ''}`}
        >
            <div className={`flex-grow flex items-center min-w-0 ${task.completed ? 'text-subtleText' : 'text-lightText'}`}>
                {task.completed && <CheckCircle className="h-5 w-5 mr-2 text-green-500 flex-shrink-0" />}
                <span className={`relative truncate ${task.completed ? 'line-through decoration-slate-500 decoration-2' : ''}`}>
                    {task.name}
                </span>
                <span className={`ml-2 text-xs flex-shrink-0 ${task.completed ? 'text-slate-500 line-through decoration-slate-500 decoration-2' : 'text-muted-foreground'}`}>({task.estimatedDuration} min)</span>
            </div>

            <div className="flex items-center space-x-2 flex-shrink-0 ml-2">
                {!task.completed && !isRunning && !isPaused && (
                    <Button
                        variant="buttonGreen"
                        size="sm"
                        onClick={() => onStartTask(task.id)}
                        // Disable if any timer is active (isRunning) unless this task IS the active one but paused (isPaused = true, isRunning = false)
                        disabled={isTimerActive && !isPaused}
                        aria-label={`Start task ${task.name}`}
                    >
                        <Play className="h-4 w-4 mr-1" /> Start
                    </Button>
                )}
                {isPaused && (
                    <Button
                        variant="buttonBlue" // Or another suitable variant for Resume
                        size="sm"
                        onClick={() => onStartTask(task.id)} // onStartTask handles resume logic via handleMasterPlayPause
                        aria-label={`Resume task ${task.name}`}
                    >
                        <Pause className="h-4 w-4 mr-1" /> Resume
                    </Button>
                )}
                {isRunning && (
                    <span className="text-xs text-cyanAccent font-semibold px-2 py-1 bg-cyanAccent/10 rounded-full">Active</span>
                )}
                {task.completed && (
                    <span className="text-xs text-green-400 font-semibold px-2 py-1 bg-green-400/10 rounded-full">Done!</span>
                )}
                <Button
                    variant="destructive"
                    size="sm"
                    onClick={() => onRemoveTask(task.id)}
                    aria-label={`Remove task ${task.name}`}
                >
                    <Trash2 className="h-4 w-4" />
                </Button>
            </div>
        </li>
    );
}

export default TaskItem;
