import React, { useState } from 'react';
import { Button } from './ui/Button';
import { toast } from '../hooks/use-toast';

function TaskForm({ onAddTask }) {
    const [taskName, setTaskName] = useState('');
    const [taskDuration, setTaskDuration] = useState('30');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!taskName.trim()) {
            toast({ title: "Error", description: "Task name cannot be empty.", variant: "destructive" });
            return;
        }
        const duration = parseInt(taskDuration, 10);
        if (isNaN(duration) || duration <= 0) {
            toast({ title: "Error", description: "Please enter a valid positive number for duration.", variant: "destructive" });
            return;
        }
        onAddTask({ name: taskName, duration });
        setTaskName('');
        setTaskDuration('30');
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4">
            <div>
                <label htmlFor="taskName" className="block text-sm font-medium text-lightText mb-1">Task Name</label>
                <input
                    type="text"
                    id="taskName"
                    value={taskName}
                    onChange={(e) => setTaskName(e.target.value)}
                    placeholder="Enter task name"
                    className="w-full p-2 rounded-md bg-dark-200 border border-dark-300 focus:ring-cyanAccent focus:border-cyanAccent text-lightText"
                />
            </div>
            <div>
                <label htmlFor="taskDuration" className="block text-sm font-medium text-lightText mb-1">Duration (minutes)</label>
                <input
                    type="number"
                    id="taskDuration"
                    value={taskDuration}
                    onChange={(e) => setTaskDuration(e.target.value)}
                    placeholder="e.g., 30"
                    min="1"
                    className="w-full p-2 rounded-md bg-dark-200 border border-dark-300 focus:ring-cyanAccent focus:border-cyanAccent text-lightText"
                />
            </div>
            <Button type="submit" variant="default" className="w-full">Add Task</Button>
        </form>
    );
}

export default TaskForm;
