import React from 'react';
import TaskItem from './TaskItem';
import { Card, CardContent, CardHeader, CardTitle } from './ui/Card';
import { ListX, ClipboardList } from 'lucide-react';

function TaskList({ tasks, onRemoveTask, onStartTask, currentTaskIndex, activeTaskOriginalId, isTimerActive, isBreakTime, timeRemaining }) {
    return (
        <Card
            variant="elevated"
            hoverEffect
            className="overflow-hidden h-full"
        >
            <CardHeader className="pb-3 border-b border-dark-300/30">
                <CardTitle className="flex items-center justify-between text-base">
                    <div className="flex items-center">
                        <ClipboardList className="h-4 w-4 mr-2 text-cyanAccent" />
                        Task Queue
                    </div>
                    {tasks?.length > 0 && (
                        <div className="bg-dark-300/30 text-xs py-0.5 px-2 rounded-full text-subtleText">
                            {tasks.length} {tasks.length === 1 ? 'task' : 'tasks'}
                        </div>
                    )}
                </CardTitle>
            </CardHeader>
            <CardContent className="pt-4 h-[calc(100%-60px)]">
                {(!tasks || tasks.length === 0) ? (
                    <div className="flex flex-col items-center justify-center h-full py-8 px-3">
                        <div className="bg-dark-200/40 rounded-full p-4 mb-3">
                            <ListX className="h-8 w-8 text-subtleText/90" />
                        </div>
                        <p className="text-subtleText/90 text-center text-sm font-medium">No tasks yet</p>
                        <p className="text-xs text-subtleText/60 text-center mt-1 max-w-[200px]">
                            Add tasks from the form to start working on them
                        </p>
                    </div>
                ) : (
                    <ul className="space-y-2.5 max-h-[450px] overflow-y-auto pb-2 pt-1 px-0.5 custom-scrollbar" id="task-list">
                        {tasks.map((task) => (
                            <TaskItem
                                key={task.id}
                                task={task}
                                onRemoveTask={onRemoveTask}
                                onStartTask={onStartTask}
                                currentTaskIndex={currentTaskIndex}
                                activeTaskOriginalId={activeTaskOriginalId}
                                isTimerActive={isTimerActive}
                                isBreakTime={isBreakTime}
                                timeRemaining={timeRemaining}
                            />
                        ))}
                    </ul>
                )}
            </CardContent>
        </Card>
    );
}

export default TaskList;
