import React from 'react';
import { Button } from './ui/Button';
import { Badge } from './ui/badge'; // Assuming you have a Badge component
import { Play, Pause, Trash2, CheckCircle } from 'lucide-react';

function TaskItem({ task, onRemoveTask, onStartTask, activeTaskOriginalId, isTimerActive, isBreakTime, timeRemaining }) {
    const isRunning = isTimerActive && !isBreakTime && task.id === activeTaskOriginalId;
    const isPaused = !isTimerActive && task.id === activeTaskOriginalId && task.started && !task.completed && timeRemaining > 0;
    const isActive = isRunning || isPaused;

    const handleStartOrResume = () => {
        if (!task.completed) {
            onStartTask(task.id);
        }
    };

    return (
        <li
            className={`p-3 rounded-md shadow flex items-center justify-between ring-1 ring-dark-300 transition-all duration-200 ease-in-out
        ${isActive ? 'bg-cyanAccent/10 ring-cyanAccent' : 'bg-dark-200 hover:shadow-lg hover:ring-cyanAccent/50'}
        ${task.completed ? 'opacity-60 bg-dark-100' : ''}
      `}
        >
            <div className="flex-grow min-w-0 mr-2">
                <div className="flex items-center">
                    <span
                        className={`font-medium text-lightText truncate ${task.completed ? 'line-through' : ''}`}
                        title={task.name}
                    >
                        {task.name}
                    </span>
                    {task.completed && <CheckCircle className="h-4 w-4 ml-2 text-green-500 flex-shrink-0" />}
                </div>
                <p className="text-xs text-subtleText">
                    Est: {task.estimatedDuration} min
                    {task.started && !task.completed && ', Spent: '}
                    {task.started && !task.completed && task.timeSpentSeconds > 0 &&
                        `${Math.floor(task.timeSpentSeconds / 60)}m ${task.timeSpentSeconds % 60}s`
                    }
                </p>
            </div>

            <div className="flex items-center space-x-2 flex-shrink-0">
                {task.completed ? (
                    <Badge variant="success">Done!</Badge>
                ) : isActive ? (
                    isRunning ? (
                        <Badge variant="default">Active</Badge>
                    ) : (
                        <Badge variant="warning">Paused</Badge>
                    )
                ) : null}

                {!task.completed && (
                    <>
                        {isPaused ? (
                            <Button variant="outline" size="sm" onClick={handleStartOrResume} aria-label="Resume task">
                                <Play className="h-4 w-4" />
                            </Button>
                        ) : !isRunning ? (
                            <Button variant="outline" size="sm" onClick={handleStartOrResume} aria-label="Start task">
                                <Play className="h-4 w-4" />
                            </Button>
                        ) : null}
                        {/* Pause button might be handled by a global pause button in App.js, 
                but if task-specific pause is needed, it would go here. 
                For now, App.js handles master play/pause. */}
                    </>
                )}

                <Button
                    variant="destructive"
                    size="sm"
                    onClick={() => onRemoveTask(task.id)}
                    aria-label={`Remove ${task.name}`}
                    disabled={isActive}
                >
                    <Trash2 className="h-4 w-4" />
                </Button>
            </div>
        </li>
    );
}

export default TaskItem;
