import React from 'react';
import { Card, CardContent } from './ui/Card';
import { Button } from './ui/Button';
import { Clock, Play, Pause, SkipForward, Check, AlertTriangle } from 'lucide-react';

const Timer = ({
    currentTask,
    timeRemaining,
    isTimerActive,
    isBreakTime,
    onPlayPause,
    onSkip,
    onComplete,
    onExtend
}) => {
    // Format the time remaining as MM:SS
    const formatTime = (seconds) => {
        const mins = Math.floor(seconds / 60);
        const secs = seconds % 60;
        return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
    };

    // Get the right colors based on timer state
    const getTimerColors = () => {
        if (isBreakTime) return { bg: 'bg-accent-green/10', text: 'text-accent-green', accent: 'accent-green' };
        if (timeRemaining < 60) return { bg: 'bg-buttonRed/10', text: 'text-buttonRed', accent: 'buttonRed' };
        return { bg: 'bg-cyanAccent/10', text: 'text-cyanAccent', accent: 'cyanAccent' };
    };

    const colors = getTimerColors();

    // Calculate progress percentage
    const getProgressPercentage = () => {
        if (!currentTask) return 0;
        const totalSeconds = isBreakTime ? (5 * 60) : (currentTask.estimatedDuration * 60);
        return ((totalSeconds - timeRemaining) / totalSeconds) * 100;
    };

    const progressPercentage = getProgressPercentage();

    return (
        <Card
            variant="elevated"
            className="overflow-hidden"
            hoverEffect
        >
            <CardContent className="p-0">
                <div className="relative overflow-hidden">
                    {/* Progress bar */}
                    <div className="absolute bottom-0 left-0 w-full h-1 bg-dark-300/30">
                        <div
                            className={`h-full ${isBreakTime ? 'bg-accent-green' : 'bg-cyanAccent'}`}
                            style={{ width: `${progressPercentage}%` }}
                        ></div>
                    </div>

                    {/* Main timer content */}
                    <div className="p-6 pt-8 pb-8 flex flex-col items-center">
                        {/* Timer status */}
                        <div className={`text-sm font-medium mb-3 ${colors.text} flex items-center`}>
                            {isBreakTime ? (
                                <>
                                    <div className="w-2 h-2 rounded-full bg-accent-green mr-2 animate-pulse"></div>
                                    Break Time
                                </>
                            ) : currentTask ? (
                                isTimerActive ? (
                                    <>
                                        <div className="w-2 h-2 rounded-full bg-cyanAccent mr-2 animate-pulse"></div>
                                        In Progress
                                    </>
                                ) : (
                                    <>
                                        <AlertTriangle className="w-4 h-4 mr-1.5 text-accent-yellow" />
                                        Paused
                                    </>
                                )
                            ) : (
                                'No Active Task'
                            )}
                        </div>

                        {/* Task title */}
                        {currentTask && (
                            <h2 className="text-lg font-semibold text-lightText mb-5 text-center">
                                {currentTask.name}
                            </h2>
                        )}

                        {/* Clock display */}
                        <div className={`flex justify-center items-center ${colors.bg} p-5 rounded-full mb-6 w-36 h-36`}>
                            <div className={`text-4xl font-bold ${colors.text} flex items-center`}>
                                {formatTime(timeRemaining)}
                            </div>
                        </div>

                        {/* Controls */}
                        <div className="flex items-center justify-center space-x-3">
                            {currentTask && (
                                <>
                                    <Button
                                        variant={isTimerActive ? "outline" : "gradient"}
                                        size="lg"
                                        onClick={onPlayPause}
                                        className="rounded-full w-14 h-14 p-0 flex items-center justify-center"
                                    >
                                        {isTimerActive ? (
                                            <Pause className="h-6 w-6" />
                                        ) : (
                                            <Play className="h-6 w-6 ml-0.5" />
                                        )}
                                    </Button>

                                    {!isBreakTime && (
                                        <Button
                                            variant="subtle"
                                            size="icon"
                                            onClick={onComplete}
                                            className="rounded-full"
                                        >
                                            <Check className="h-5 w-5" />
                                        </Button>
                                    )}

                                    <Button
                                        variant="subtle"
                                        size="icon"
                                        onClick={onSkip}
                                        className="rounded-full"
                                    >
                                        <SkipForward className="h-5 w-5" />
                                    </Button>
                                </>
                            )}

                            {!currentTask && (
                                <div className="text-center text-subtleText py-2">
                                    Select a task to begin
                                </div>
                            )}
                        </div>

                        {/* Timer details */}
                        {currentTask && (
                            <div className="flex justify-between w-full mt-6 text-xs text-subtleText">
                                <div className="flex items-center">
                                    <Clock className="h-3.5 w-3.5 mr-1.5" />
                                    {isBreakTime ? 'Break: 5 min' : `Duration: ${currentTask.estimatedDuration} min`}
                                </div>

                                {!isBreakTime && (
                                    <button
                                        onClick={onExtend}
                                        className="text-xs text-accent-yellow hover:text-accent-yellow/80 transition-colors"
                                    >
                                        + 1 minute
                                    </button>
                                )}
                            </div>
                        )}
                    </div>
                </div>
            </CardContent>
        </Card>
    );
};

export default Timer;
