import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cva } from "class-variance-authority";
import { cn } from "../../lib/utils"; // Adjusted path

const buttonVariants = cva(
    "relative inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium transition-all duration-200 ease-in-out focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 select-none",
    {
        variants: {
            variant: {
                default: "bg-primary-500 text-white hover:bg-primary-600 shadow-md hover:shadow-lg",
                destructive: "bg-buttonRed text-white hover:bg-buttonRedHover shadow-md hover:shadow-lg",
                outline: "border border-dark-300 bg-dark-200/60 text-lightText hover:bg-dark-200 hover:border-cyanAccent/70",
                secondary: "bg-dark-100 text-lightText hover:bg-dark-200/80 shadow-md hover:shadow-lg",
                ghost: "bg-transparent text-lightText hover:bg-dark-200/50 hover:text-white",
                link: "text-cyanAccent underline-offset-4 hover:underline p-0 h-auto shadow-none",
                // Custom variants
                timerAccent: "bg-timerAccent text-dark-600 hover:bg-accent-teal shadow-md hover:shadow-lg",
                buttonBlue: "bg-buttonBlue text-white hover:bg-buttonBlueHover shadow-md hover:shadow-lg",
                buttonGreen: "bg-buttonGreen text-white hover:bg-buttonGreenHover shadow-md hover:shadow-lg",
                buttonGray: "bg-buttonGray text-white hover:bg-buttonGrayHover shadow-md hover:shadow-lg",
                gradient: "bg-gradient-to-r from-cyanAccent to-brightAccent text-white hover:opacity-95 shadow-md hover:shadow-lg",
                glassy: "bg-white/10 backdrop-blur-md border border-white/20 text-white hover:bg-white/20 shadow-md hover:shadow-lg",
                subtle: "bg-dark-200/50 text-subtleText hover:text-lightText hover:bg-dark-200/80 shadow-sm",
                icon: "bg-transparent text-subtleText hover:text-lightText p-2 h-auto shadow-none hover:bg-dark-200/40 rounded-full",
                iconAccent: "bg-dark-200/60 text-cyanAccent hover:text-accent-teal p-2 h-auto shadow-none hover:bg-dark-200/80 rounded-full",
            },
            size: {
                default: "h-10 px-4 py-2",
                xs: "h-7 px-2 py-1 text-xs rounded",
                sm: "h-9 rounded-md px-3 py-2 text-sm",
                md: "h-10 rounded-md px-4 py-2",
                lg: "h-11 rounded-md px-6 py-2.5",
                xl: "h-12 rounded-md px-8 py-3 text-base",
                icon: "h-10 w-10 p-2 rounded-full",
                iconSm: "h-8 w-8 p-1.5 rounded-full",
                iconXs: "h-6 w-6 p-1 rounded-full text-xs",
            },
            isActive: {
                true: "ring-2 ring-cyanAccent/70",
            }
        },
        defaultVariants: {
            variant: "default",
            size: "default",
            isActive: false,
        },
    }
);

const Button = React.forwardRef(({
    className,
    variant,
    size,
    asChild = false,
    isActive,
    isLoading,
    loadingText = "Loading...",
    leftIcon,
    rightIcon,
    children,
    ...props
}, ref) => {
    const Comp = asChild ? Slot : "button";

    return (
        <Comp
            className={cn(buttonVariants({ variant, size, isActive, className }))}
            ref={ref}
            disabled={props.disabled || isLoading}
            {...props}>

            {isLoading ? (
                <span className="flex items-center justify-center">
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    {loadingText}
                </span>
            ) : (
                <span className="flex items-center justify-center">
                    {leftIcon && <span className="mr-2">{leftIcon}</span>}
                    {children}
                    {rightIcon && <span className="ml-2">{rightIcon}</span>}
                </span>
            )}

            {variant === 'gradient' && (
                <span className="absolute inset-0 rounded-md overflow-hidden">
                    <span className="absolute inset-0 rounded-md bg-gradient-to-r from-cyanAccent to-brightAccent opacity-0 group-hover:opacity-20 transition-opacity duration-300"></span>
                </span>
            )}
        </Comp>
    );
});

Button.displayName = "Button";

export { Button, buttonVariants };
