import React from 'react';
import { Timer, ListChecks, BrainCircuit, Settings, PieChart } from 'lucide-react';

const Navigation = ({ activeView, onChangeView, score }) => {
    const navItems = [
        { id: 'focus', label: 'Focus', icon: Timer, color: 'cyanAccent' },
        { id: 'plan', label: 'Plan', icon: ListChecks, color: 'accent-green' },
        { id: 'spirals', label: 'Spirals', icon: BrainCircuit, color: 'accent-purple' },
        { id: 'dashboard', label: 'Stats', icon: PieChart, color: 'accent-yellow' },
        { id: 'settings', label: 'Settings', icon: Settings, color: 'gray-400' },
    ];

    return (
        <div className="bg-dark-100 rounded-lg shadow-md overflow-hidden mb-6">
            <div className="flex justify-between items-center">
                {/* App branding */}
                <div className="flex items-center px-5 py-4">
                    <div className="flex items-center justify-center h-9 w-9 rounded-full bg-gradient-to-br from-cyanAccent to-brightAccent shadow-md mr-3">
                        <Timer className="h-5 w-5 text-white" />
                    </div>
                    <div>
                        <h1 className="text-xl font-bold text-gradient">TimeBoxing</h1>
                        <div className="flex items-center text-xs text-subtleText mt-0.5">
                            <span className="mr-1.5">Score:</span>
                            <span className="font-semibold text-accent-green">{score} pts</span>
                        </div>
                    </div>
                </div>

                {/* Navigation links */}
                <nav className="flex items-center pr-2">
                    {navItems.map((item) => (
                        <button
                            key={item.id}
                            onClick={() => onChangeView(item.id)}
                            className={`relative px-4 py-3 transition-all duration-300 flex flex-col items-center text-xs font-medium group
                                ${activeView === item.id ? `text-${item.color}` : 'text-subtleText hover:text-lightText'}`}
                        >
                            <item.icon className={`h-5 w-5 mb-1 ${activeView === item.id ? `text-${item.color}` : 'text-subtleText group-hover:text-lightText'}`} />
                            {item.label}

                            {/* Active indicator */}
                            {activeView === item.id && (
                                <span className={`absolute bottom-0 left-0 right-0 h-0.5 bg-${item.color}`}></span>
                            )}
                        </button>
                    ))}
                </nav>
            </div>
        </div>
    );
};

export default Navigation;
