import React from 'react';
import { useToast } from '../hooks/use-toast';
import { cn } from '../utils'; // Adjusted path

// Basic Toast component styling - you can enhance this
const toastBaseClasses = "fixed top-0 right-0 m-4 p-4 rounded-md shadow-lg transition-all duration-300 ease-in-out";
const toastVariants = {
    default: "bg-gray-800 text-white border border-gray-700",
    destructive: "bg-red-600 text-white border border-red-700",
    success: "bg-green-600 text-white border border-green-700",
};

function Toast({ toast: t }) {
    const { dismiss } = useToast();
    const [visible, setVisible] = React.useState(false);

    React.useEffect(() => {
        // Mount animation
        setVisible(true);
    }, []);

    React.useEffect(() => {
        if (!t.visible) {
            // Unmount animation trigger
            setVisible(false);
        }
    }, [t.visible]);

    const variantClass = toastVariants[t.variant] || toastVariants.default;

    if (!t.id) return null; // Should not happen with proper ID generation

    return (
        <div
            className={cn(
                toastBaseClasses,
                variantClass,
                visible ? "opacity-100 translate-x-0" : "opacity-0 translate-x-full"
            )}
            style={{ zIndex: 1000 }} // Ensure it's on top
        >
            <div className="flex justify-between items-start">
                <div>
                    {t.title && <div className="font-semibold">{t.title}</div>}
                    {t.description && <div className="text-sm">{t.description}</div>}
                </div>
                <button
                    onClick={() => dismiss(t.id)}
                    className="ml-4 text-sm font-semibold opacity-70 hover:opacity-100"
                    aria-label="Dismiss notification"
                >
                    âœ•
                </button>
            </div>
        </div>
    );
}

function Toaster() {
    const { toasts } = useToast();

    if (!toasts.length) {
        return null;
    }

    return (
        <div
            className="fixed top-0 right-0 z-[100] flex flex-col p-4 space-y-2"
            aria-live="assertive"
            aria-atomic="true"
        >
            {toasts.map(function (toast) {
                return <Toast key={toast.id} toast={toast} />;
            })}
        </div>
    );
}

export { Toaster };
