import React, { useState } from 'react';
import { Button } from './ui/Button';
import { toast } from '../hooks/use-toast';

function SpiralForm({ onAddSpiral }) {
    const [spiralName, setSpiralName] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!spiralName.trim()) {
            toast({ title: "Error", description: "Spiral name cannot be empty.", variant: "destructive" });
            return;
        }
        onAddSpiral(spiralName);
        setSpiralName('');
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-3 mb-4">
            <div>
                <label htmlFor="spiralName" className="block text-sm font-medium text-lightText mb-1">New Spiral Idea</label>
                <input
                    type="text"
                    id="spiralName"
                    value={spiralName}
                    onChange={(e) => setSpiralName(e.target.value)}
                    placeholder="Enter a new idea or deferred task"
                    className="w-full p-2 rounded-md bg-dark-200 border border-dark-300 focus:ring-cyanAccent focus:border-cyanAccent text-lightText"
                />
            </div>
            <Button type="submit" variant="default" className="w-full">Add Spiral</Button>
        </form>
    );
}

export default SpiralForm;
