import React from 'react';
import TaskItem from './TaskItem';

function TaskList({ tasks, onRemoveTask, onStartTask, currentTaskIndex, activeTaskOriginalId, isTimerActive, isBreakTime }) {
    if (!tasks || tasks.length === 0) {
        return <p className="text-sm text-subtleText text-center py-4">No tasks yet. Add some to get started!</p>;
    }

    return (
        <ul className="space-y-3 max-h-96 overflow-y-auto pr-1" id="task-list">
            {tasks.map((task, index) => (
                <TaskItem
                    key={task.id}
                    task={task}
                    onRemoveTask={onRemoveTask}
                    onStartTask={onStartTask}
                    currentTaskIndex={currentTaskIndex}
                    activeTaskOriginalId={activeTaskOriginalId}
                    isTimerActive={isTimerActive}
                    isBreakTime={isBreakTime}
                />
            ))}
        </ul>
    );
}

export default TaskList;
