import React from 'react';
import { Button } from './ui/Button';
import { Badge } from './ui/Badge';
import { Trash2, Play, Check, Pause, RotateCcw } from 'lucide-react';
import { cn } from '../lib/utils';

const TaskItem = ({
    task,
    onRemoveTask,
    onStartTask,
    activeTaskOriginalId,
    isTimerActive,
    isBreakTime,
    timeRemaining,
    theme
}) => {
    const isActiveCore = task.originalId === activeTaskOriginalId && !task.completed;
    const isRunning = isActiveCore && isTimerActive && !isBreakTime;
    const isPaused = isActiveCore && (!isTimerActive || isBreakTime) && task.duration * 60 > timeRemaining && timeRemaining > 0;

    const handleStart = () => {
        if (!isRunning && !isPaused) {
            onStartTask(task.originalId);
        }
    };

    const handleResume = () => {
        if (isPaused) {
            onStartTask(task.originalId);
        }
    };

    const taskNameColor = theme === 'dark' ? 'text-lightText' : 'text-darkText';
    const taskDurationColor = theme === 'dark' ? 'text-subtleText' : 'text-slate-500';
    const cardBgColor = theme === 'dark' ? 'bg-dark-200' : 'bg-slate-100';
    const cardRingColor = theme === 'dark' ? 'ring-dark-300' : 'ring-slate-300';

    const activeBadgeVariant = theme === 'dark' ? 'secondary' : 'default';
    const doneBadgeVariant = theme === 'dark' ? 'default' : 'outline';
    const pausedBadgeVariant = theme === 'dark' ? 'outline' : 'default';

    return (
        <div
            className={cn(
                `flex items-center justify-between p-3 rounded-lg shadow-sm ring-1 ${cardBgColor} ${cardRingColor}`,
                task.completed && (theme === 'dark' ? 'opacity-60 bg-dark-100' : 'opacity-70 bg-slate-50'),
                (isRunning || isPaused) && (theme === 'dark' ? 'ring-2 ring-cyanAccent' : 'ring-2 ring-blue-500')
            )}
        >
            <div className="flex-grow min-w-0 mr-3">
                <p className={`font-medium truncate ${taskNameColor}`}>{task.name}</p>
                <p className={`text-xs ${taskDurationColor}`}>{task.duration} min</p>
            </div>

            <div className="flex items-center space-x-2 flex-shrink-0">
                {task.completed ? (
                    <Badge variant={doneBadgeVariant} className={cn(theme === 'light' && 'border-green-500 text-green-600', 'py-1 px-2 text-xs')}>Done!</Badge>
                ) : isRunning ? (
                    <Badge variant={activeBadgeVariant} className={cn(theme === 'dark' ? 'text-cyanAccent border-cyanAccent' : 'bg-blue-500 text-white', 'py-1 px-2 text-xs')}>Active</Badge>
                ) : isPaused ? (
                    <Badge variant={pausedBadgeVariant} className={cn(theme === 'dark' ? 'text-timerAccent border-timerAccent' : 'border-yellow-500 text-yellow-600', 'py-1 px-2 text-xs')}>Paused</Badge>
                ) : (
                    <Button
                        onClick={handleStart}
                        size="sm"
                        variant={theme === 'dark' ? 'buttonGreen' : 'primary_light'}
                        className="px-3 py-1 text-xs h-auto"
                        disabled={isTimerActive}
                    >
                        <Play className="h-3 w-3 mr-1" /> Start
                    </Button>
                )}

                {isPaused && (
                    <Button
                        onClick={handleResume}
                        size="sm"
                        variant={theme === 'dark' ? 'buttonBlue' : 'primary_light_outline'}
                        className="px-3 py-1 text-xs h-auto">
                        <RotateCcw className="h-3 w-3 mr-1" /> Resume
                    </Button>
                )}

                {!isRunning && !isPaused && !task.completed && (
                    <Button onClick={() => onRemoveTask(task.id)} size="sm" variant={theme === 'dark' ? 'ghost' : 'outline'} className={cn("px-2 py-1 text-xs h-auto", theme === 'light' && "text-red-500 border-red-300 hover:bg-red-500/10 hover:border-red-500")}>
                        <Trash2 className="h-3 w-3" />
                    </Button>
                )}
            </div>
        </div>
    );
};

export default TaskItem;
