import React from 'react';
import { cva } from 'class-variance-authority';

const cardVariants = cva(
    "rounded-lg transition-all duration-300 backdrop-blur-sm",
    {
        variants: {
            variant: {
                default: "bg-gray-800/80 border border-gray-700/50 shadow-lg",
                glass: "glass-card shadow-xl",
                highlight: "bg-gradient-to-br from-gray-800/90 to-gray-900/90 border border-cyan-900/50 shadow-lg shadow-cyan-900/10",
            },
            size: {
                sm: "p-3",
                md: "p-4",
                lg: "p-6",
            },
        },
        defaultVariants: {
            variant: "default",
            size: "md",
        },
    }
);

const Card = React.forwardRef(({ className, variant, size, interactive = false, children, ...props }, ref) => {
    return (
        <div
            ref={ref}
            className={`${cardVariants({ variant, size })} ${interactive ? 'card-hover' : ''} ${className || ''}`}
            {...props}
        >
            {children}
        </div>
    );
});

Card.displayName = "Card";

const CardHeader = React.forwardRef(({ className, children, ...props }, ref) => {
    return (
        <div
            ref={ref}
            className={`flex flex-col space-y-1.5 pb-4 ${className || ''}`}
            {...props}
        >
            {children}
        </div>
    );
});

CardHeader.displayName = "CardHeader";

const CardTitle = React.forwardRef(({ className, children, ...props }, ref) => {
    return (
        <h3
            ref={ref}
            className={`text-xl font-semibold tracking-tight ${className || ''}`}
            {...props}
        >
            {children}
        </h3>
    );
});

CardTitle.displayName = "CardTitle";

const CardDescription = React.forwardRef(({ className, children, ...props }, ref) => {
    return (
        <p
            ref={ref}
            className={`text-gray-400 text-sm ${className || ''}`}
            {...props}
        >
            {children}
        </p>
    );
});

CardDescription.displayName = "CardDescription";

const CardContent = React.forwardRef(({ className, children, ...props }, ref) => {
    return (
        <div
            ref={ref}
            className={`${className || ''}`}
            {...props}
        >
            {children}
        </div>
    );
});

CardContent.displayName = "CardContent";

const CardFooter = React.forwardRef(({ className, children, ...props }, ref) => {
    return (
        <div
            ref={ref}
            className={`flex items-center pt-4 ${className || ''}`}
            {...props}
        >
            {children}
        </div>
    );
});

CardFooter.displayName = "CardFooter";

export { Card, CardHeader, CardTitle, CardDescription, CardContent, CardFooter };
