import React, { useState } from 'react';
import { Button } from './ui/Button';
import { Input } from './ui/Input';
import { Label } from './ui/Label';
import { toast } from '../hooks/use-toast';
import { Clock, FileText, Plus, RotateCcw, BrainCircuit, PlusCircle } from 'lucide-react';

// Generic FormField component for DRY code in TaskForm and SpiralForm
const FormField = ({ id, label, icon: Icon, type, placeholder, value, onChange, onFocus, onBlur, showClearButton, onClear }) => (
    <div className={`transition-all duration-300 ${onFocus ? 'group' : ''}`}>
        <Label
            htmlFor={id}
            className="text-subtleText/80 mb-1.5 flex items-center text-[11px] font-medium group-focus-within:text-cyanAccent transition-colors duration-300"
        >
            <Icon className="h-3.5 w-3.5 mr-1.5 text-cyanAccent/70 group-focus-within:text-cyanAccent transition-colors duration-300" />
            {label}
        </Label>
        <div className="relative">
            <Input
                id={id}
                type={type}
                placeholder={placeholder}
                value={value}
                onChange={onChange}
                onFocus={onFocus}
                onBlur={onBlur}
                className="bg-dark-200/50 border-dark-300/30 focus:ring-cyanAccent/50 focus:border-cyanAccent/70 text-xs pl-3 pr-8 py-2 rounded-md shadow-inner transition-all duration-300 hover:bg-dark-200/70 focus:bg-dark-200/70 placeholder:text-subtleText/50"
            />
            {showClearButton && value && (
                <button
                    type="button"
                    onClick={onClear}
                    className="absolute right-2.5 top-1/2 transform -translate-y-1/2 text-subtleText/60 hover:text-lightText transition-colors p-0.5"
                    aria-label="Clear input"
                >
                    <RotateCcw className="h-3 w-3" />
                </button>
            )}
        </div>
    </div>
);

function TaskForm({ onAddTask }) {
    const [taskName, setTaskName] = useState('');
    const [taskDuration, setTaskDuration] = useState('');
    const [isFocused, setIsFocused] = useState({ name: false, duration: false });

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!taskName.trim() || !taskDuration || parseInt(taskDuration, 10) <= 0) {
            toast({
                title: "Invalid Input",
                description: "Please enter a valid task name and a positive duration.",
                variant: "destructive",
                duration: 2000,
            });
            return;
        }
        onAddTask({
            name: taskName.trim(),
            duration: parseInt(taskDuration, 10)
        });
        setTaskName('');
        setTaskDuration('');
        setIsFocused({ name: false, duration: false });
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-3.5">
            <FormField
                id="task-name-input"
                label="Task Description"
                icon={FileText}
                type="text"
                placeholder="E.g., Conquer the world... one task at a time"
                value={taskName}
                onChange={(e) => setTaskName(e.target.value)}
                onFocus={() => setIsFocused(prev => ({ ...prev, name: true }))}
                onBlur={() => setIsFocused(prev => ({ ...prev, name: false }))}
                showClearButton
                onClear={() => setTaskName('')}
            />
            <FormField
                id="task-duration-input"
                label="Duration (minutes)"
                icon={Clock}
                type="number"
                placeholder="E.g., 25"
                value={taskDuration}
                onChange={(e) => setTaskDuration(e.target.value)}
                onFocus={() => setIsFocused(prev => ({ ...prev, duration: true }))}
                onBlur={() => setIsFocused(prev => ({ ...prev, duration: false }))}
                showClearButton
                onClear={() => setTaskDuration('')}
            />
            <Button
                type="submit"
                variant="buttonBlue"
                size="sm"
                className="w-full bg-gradient-to-r from-cyanAccent/90 to-brightAccent/90 hover:from-cyanAccent hover:to-brightAccent transition-all duration-300 flex items-center justify-center shadow-md hover:shadow-lg py-2.5 text-xs font-semibold tracking-wide"
            >
                <Plus className="h-3.5 w-3.5 mr-1.5" />
                Add Task
            </Button>
        </form>
    );
}

export default TaskForm;
