import React, { useState } from 'react';
import { Button } from './ui/Button';
import { Input } from './ui/Input';
import { Label } from './ui/Label';
import { Card, CardContent, CardHeader, CardTitle } from './ui/Card';
import { toast } from '../hooks/use-toast';
import { Clock, FileText, Plus, Calendar } from 'lucide-react';

function TaskForm({ onAddTask }) {
    const [taskName, setTaskName] = useState('');
    const [taskDuration, setTaskDuration] = useState('');
    const [isFocused, setIsFocused] = useState({ name: false, duration: false });

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!taskName.trim() || !taskDuration || parseInt(taskDuration, 10) <= 0) {
            toast({
                title: "Invalid Input",
                description: "Please enter a valid task name and a positive duration.",
                variant: "destructive",
            });
            return;
        }
        onAddTask({
            name: taskName.trim(),
            duration: parseInt(taskDuration, 10)
        });
        setTaskName('');
        setTaskDuration('');
    };

    return (
        <Card
            variant="elevated"
            hoverEffect
            className="overflow-hidden"
        >
            <CardHeader className="pb-3 border-b border-dark-300/30">
                <CardTitle className="flex items-center text-base">
                    <Calendar className="h-4 w-4 mr-2 text-cyanAccent" />
                    Plan Your Tasks
                </CardTitle>
            </CardHeader>
            <CardContent className="pt-4">
                <form onSubmit={handleSubmit} className="space-y-4">
                    <div className={`transition-all duration-300 ${isFocused.name ? 'scale-[1.01]' : ''}`}>
                        <Label
                            htmlFor="task-name-input"
                            className="text-subtleText mb-1.5 flex items-center text-xs font-medium"
                        >
                            <FileText className="h-3.5 w-3.5 mr-1 text-cyanAccent" />
                            Task Description
                        </Label>
                        <Input
                            id="task-name-input"
                            type="text"
                            placeholder="E.g., Write blog post"
                            value={taskName}
                            onChange={(e) => setTaskName(e.target.value)}
                            onFocus={() => setIsFocused(prev => ({ ...prev, name: true }))}
                            onBlur={() => setIsFocused(prev => ({ ...prev, name: false }))}
                            variant="filled"
                            leftIcon={<FileText className="h-4 w-4 text-subtleText/70" />}
                            className="transition-all duration-300"
                            autoComplete="off"
                        />
                    </div>
                    <div className={`transition-all duration-300 ${isFocused.duration ? 'scale-[1.01]' : ''}`}>
                        <Label
                            htmlFor="task-duration-input"
                            className="text-subtleText mb-1.5 flex items-center text-xs font-medium"
                        >
                            <Clock className="h-3.5 w-3.5 mr-1 text-cyanAccent" />
                            Duration (minutes)
                        </Label>
                        <Input
                            id="task-duration-input"
                            type="number"
                            placeholder="E.g., 30"
                            value={taskDuration}
                            onChange={(e) => setTaskDuration(e.target.value)}
                            onFocus={() => setIsFocused(prev => ({ ...prev, duration: true }))}
                            onBlur={() => setIsFocused(prev => ({ ...prev, duration: false }))}
                            variant="filled"
                            leftIcon={<Clock className="h-4 w-4 text-subtleText/70" />}
                            className="transition-all duration-300"
                            autoComplete="off"
                        />
                    </div>
                    <div className="pt-2">
                        <Button
                            type="submit"
                            variant="gradient"
                            size="md"
                            className="w-full shadow-md"
                            leftIcon={<Plus className="h-4 w-4" />}
                        >
                            Add Task
                        </Button>
                    </div>
                </form>
            </CardContent>
        </Card>
    );
}

export default TaskForm;
