import React from 'react';
import TaskItem from './TaskItem';

function TaskList({ tasks, onRemoveTask, onStartTask, isTimerActive }) {
    if (tasks.length === 0) {
        return <p className="text-sm text-subtleText italic">No tasks planned yet. Add some!</p>;
    }

    return (
        <ul className="space-y-3">
            {tasks.map((task) => (
                <TaskItem
                    key={task.id}
                    task={task}
                    onRemoveTask={onRemoveTask}
                    onStartTask={onStartTask}
                    isTimerActive={isTimerActive}
                />
            ))}
        </ul>
    );
}

export default TaskList;
