import React from 'react';
import { Button } from './ui/Button';
import { ListChecks, Trash2 } from 'lucide-react';

function SpiralList({ spirals, onRemoveSpiral, onMoveSpiralToTasks }) {
    if (!spirals || spirals.length === 0) {
        return <p className="text-sm text-subtleText text-center py-4">No spiral ideas yet. Add some above!</p>;
    }

    return (
        <ul className="space-y-3 max-h-80 overflow-y-auto pr-1">
            {spirals.map((spiral) => (
                <li
                    key={spiral.id}
                    className="p-3 bg-dark-200 rounded-md shadow flex justify-between items-center ring-1 ring-dark-300 hover:shadow-lg hover:ring-cyanAccent/50"
                >
                    <span className="text-lightText truncate mr-2">{spiral.name}</span>
                    <div className="flex space-x-2 flex-shrink-0">
                        <Button
                            variant="outline"
                            size="sm"
                            onClick={() => onMoveSpiralToTasks(spiral.id)}
                            aria-label={`Move ${spiral.name} to tasks`}
                        >
                            <ListChecks className="h-4 w-4" />
                        </Button>
                        <Button
                            variant="destructive"
                            size="sm"
                            onClick={() => onRemoveSpiral(spiral.id)}
                            aria-label={`Remove ${spiral.name}`}
                        >
                            <Trash2 className="h-4 w-4" />
                        </Button>
                    </div>
                </li>
            ))}
        </ul>
    );
}

export default SpiralList;
