import * as React from "react";
import { cn } from "../../lib/utils"; // Adjusted path

const Card = React.forwardRef(({
    className,
    variant = "default",
    glow = false,
    glassEffect = false,
    hoverEffect = false,
    borderStyle = "none",
    ...props
}, ref) => {
    const baseClasses = "rounded-lg text-card-foreground shadow-sm transition-all duration-300";

    // Apply variant-specific styling
    let variantClasses = "";
    if (variant === "default") {
        variantClasses = "bg-dark-100";
    } else if (variant === "elevated") {
        variantClasses = "bg-dark-100 shadow-elevated";
    } else if (variant === "outline") {
        variantClasses = "bg-dark-100/90 border border-dark-300/60";
    } else if (variant === "subtle") {
        variantClasses = "bg-dark-200/60";
    }

    // Apply border style if specified
    let borderClasses = "";
    if (borderStyle === "accent") {
        borderClasses = "border-l-4 border-l-cyanAccent";
    } else if (borderStyle === "top") {
        borderClasses = "border-t-4 border-t-cyanAccent";
    } else if (borderStyle === "bottom") {
        borderClasses = "border-b-4 border-b-cyanAccent";
    }

    // Apply effects
    const effectClasses = [
        glow ? "shadow-glow" : "",
        glassEffect ? "frosted-glass" : "",
        hoverEffect ? "hover:translate-y-[-2px] hover:shadow-elevated" : "",
    ].filter(Boolean).join(" ");

    return (
        <div
            ref={ref}
            className={cn(baseClasses, variantClasses, borderClasses, effectClasses, className)}
            {...props}
        />
    );
});

Card.displayName = "Card";

const CardHeader = React.forwardRef(({ className, ...props }, ref) => (
    <div
        ref={ref}
        className={cn("flex flex-col space-y-1.5 p-5", className)}
        {...props}
    />
));

CardHeader.displayName = "CardHeader";

const CardTitle = React.forwardRef(({ className, ...props }, ref) => (
    // eslint-disable-next-line jsx-a11y/heading-has-content
    <h3
        ref={ref}
        className={cn("text-xl font-semibold leading-none tracking-tight text-lightText", className)}
        {...props}
    />
));

CardTitle.displayName = "CardTitle";

const CardDescription = React.forwardRef(({ className, ...props }, ref) => (
    <p
        ref={ref}
        className={cn("text-sm text-subtleText", className)}
        {...props}
    />
));

CardDescription.displayName = "CardDescription";

const CardContent = React.forwardRef(({ className, ...props }, ref) => (
    <div ref={ref} className={cn("p-5 pt-0", className)} {...props} />
));

CardContent.displayName = "CardContent";

const CardFooter = React.forwardRef(({ className, ...props }, ref) => (
    <div
        ref={ref}
        className={cn("flex items-center p-5 pt-0", className)}
        {...props}
    />
));

CardFooter.displayName = "CardFooter";

export { Card, CardHeader, CardFooter, CardTitle, CardDescription, CardContent };
